import { useContext, useEffect, useState } from 'react';
import CartIcon from '../Cart/CartIcon';
import classes from './HeaderCartButton.module.css';

import CartContext from '../../store/cart-context';
const HeaderCartButton = (props) => {
  const [btnIsHighlighted, setBtnIsHighlighted] = useState(false) ;
   const CartCtx 
    = useContext(CartContext) ; const {items} = CartCtx ;
  const numberOfCartItems = items.reduce((curNumber, item)=>{return curNumber + item.amount ;
  }, 0);
 
  const btnClasses = `${classes.button}  ${btnIsHighlighted ?classes.bump:''}`;
  useEffect(()=>{
    if(items.length === 0) { return  ;}
     setBtnIsHighlighted(true) ;
     const timer  = setTimeout(()=>{
         setBtnIsHighlighted(false) ; 
     },300)
     return ()=> { //if we keep adding the items then multiple timers will be set so we use clean up function
        clearTimeout(timer) ;
     }
  },[items]) ;
  
  return (
    <button className={btnClasses} onClick={props.onClick}>
      <span className={classes.icon}>
        <CartIcon />
      </span>
      <span>Your Cart</span>
      <span className={classes.badge}>{numberOfCartItems}</span>
    </button>
  );
};

export default HeaderCartButton;
